declare module '*.scss' {
    const content: Record<string, string>;
    export default content;
}

declare module "*.gif" {
    const value: any;
    export = value;
}

declare module "*.svg" {
    const value: any;
    export = value;
}

declare module "*.png" {
    const value: any;
    export = value;
}

declare module "*.jpg" {
    const value: any;
    export = value;
}